import tcod as libtcodfrom gameclass import *FOV_ALGO = 0  #default FOV algorithmFOV_LIGHT_WALLS = TrueTORCH_RADIUS = 0fov_map=libtcod.map_new(2, 2)def new_fov(mapa):    global fov_map    fov_map = libtcod.map_new(MAP_WIDTH,MAP_HEIGHT)    X,Y=MAP_WIDTH,MAP_HEIGHT    for y in range(Y):        for x in range(X):                        libtcod.map_set_properties(fov_map, x, y, mapa[x][y].transparent, 1)    def fov_comp(where):    global fov_map        x,y=where    libtcod.map_compute_fov(fov_map, x, y, TORCH_RADIUS, FOV_LIGHT_WALLS, FOV_ALGO)    def fov_check(x,y):    global fov_map    try:                return  libtcod.map_is_in_fov(fov_map, x, y)    except:        return 0    